<template>
  <el-dialog
          :modal-append-to-body='false'
          :close-on-click-modal="false"
          :visible.sync="modal.visible"
          v-loading="modal.loading||false"
          :width="width">
    <div slot="title" class="flex center">
      <slot name="title" />
    </div>
    <slot/>
    <div slot="footer" class="flex center footer" v-if="!noFooter">
      <el-button type="info" plain @click="cancel">取消</el-button>
      <el-button type="primary" @click="confirm">确定</el-button>
    </div>
  </el-dialog>
</template>
<script lang="ts">
export default {
  props: {
    confirm: {
      type: Function,
      default: () => { },
    },
    close: {
      type: Function,
      default: () => {},
    },
    modal: {
      type: Object,
      default: { visible: true },
    },
    width: {
      type: String,
      default: '',
    },
    noFooter: {
      type: Boolean,
      default: false,
    },
  },
  setup(props: any) {
    function cancel() {
      props.close();
      props.modal.visible = false;
    }
    return{
      cancel,
    };
  },
};
</script>
<style lang="scss" scoped>
  .footer{
    .el-button:first-child{
      margin-right: 2rem;
    }
  }
  .el-dialog{
    z-index: 5000;
  }
</style>
